use std::collections::HashSet;
use std::sync::OnceLock;

static FREAKY_PHRASES: OnceLock<HashSet<&'static str>> = OnceLock::new();

fn get_freaky_phrases() -> &'static HashSet<&'static str> {
	FREAKY_PHRASES.get_or_init(|| {
		HashSet::from([
			"wyd",
			"fuck me",
			"fucked",
			"wtf",
			"u up",
			"u up?",
			"sex",
			"hu",
			"makeout",
			"🍆",
			"🍑",
			"🤤",
			"come over",
			"netflix and chill",
			"nudes",
			"send nudes"
		])
	})
}

pub fn is_freaky_phrase(phrase: &str) -> bool {
	get_freaky_phrases().contains(&phrase.to_lowercase().as_str())
}
